import Prism from 'prismjs';
import React, { useState, useCallback, useMemo } from 'react';
import { Slate, Editable, withReact } from 'slate-react';
import { Text, createEditor, Element, Descendant } from 'slate';
import { withHistory } from 'slate-history';

import styles from './../Style/Components/Editor.module.css';

import { Send } from './../Scripts/Chatter';
Prism.languages.markdown=Prism.languages.extend("markup",{});
Prism.languages.insertBefore("markdown","prolog", {
  blockquote:{
    pattern:/^>(?:[\t ]*>)*/m,
    alias:"punctuation"
  },
  code:[
    {
      pattern:/^(?: {4}|\t).+/m,
      alias:"keyword"
    },
    {
      pattern:/`\`.+?`\`|`[^`\n]+`/,
      alias: "keyword"
    }
  ],
  title:[
    {
      pattern:/\w+.*(?:\r?\n|\r)(?:==+|--+)/,
      alias:"important",
      inside:{
        punctuation:/==+$|--+$/
      }
    },
    {
      pattern:/(^\s*)#+.+/m,
      lookbehind:!0,
      alias:"important",
      inside:{
        punctuation:/^#+|#+$/
      }
    }
  ],
  hr:{
    pattern:/(^\s*)([*-])([\t ]*\2){2,}(?=\s*$)/m,
    lookbehind:!0,
    alias:"punctuation"
  },
  list:{
    pattern:/(^\s*)(?:[*+-]|\d+\.)(?=[\t ].)/m,
    lookbehind:!0,
    alias:"punctuation"
  },
  "url-reference":{
    pattern:/!?\[[^\]]+\]:[\t ]+(?:\S+|<(?:\\.|[^>\\])+>)(?:[\t ]+(?:"(?:\\.|[^"\\])*"|'(?:\\.|[^'\\])*'|\((?:\\.|[^)\\])*\)))?/,
    inside:{
      variable:{
        pattern:/^(!?\[)[^\]]+/,
        lookbehind:!0
      },
      'string':/(?:"(?:\\.|[^"\\])*"|'(?:\\.|[^'\\])*'|\((?:\\.|[^)\\])*\))$/,
      punctuation:/^[\[\]!:]|[<>]/
    },
    alias:"url"
  },
  bold:{
    pattern:/(^|[^\\])(\*\*|__)(?:(?:\r?\n|\r)(?!\r?\n|\r)|.)+?\2/,
    lookbehind:!0,
    inside:{
      punctuation:/^\*\*|^__|\*\*$|__$/
    }
  },
  italic:{
    pattern:/(^|[^\\])([*_])(?:(?:\r?\n|\r)(?!\r?\n|\r)|.)+?\2/,
    lookbehind:!0,
    inside:{
      punctuation:/^[*_]|[*_]$/
    }
  },
  url:{
    pattern:/!?\[[^\]]+\](?:\([^\s)]+(?:[\t ]+"(?:\\.|[^"\\])*")?\)| ?\[[^\]\n]*\])/,
    inside:{
      variable:{
        pattern:/(!?\[)[^\]]+(?=\]$)/,
        lookbehind:!0
      },
      'string':{
        pattern:/"(?:\\.|[^"\\])*"(?=\)$)/
      }
    }
  }
});
Prism.languages.markdown.bold.inside.url=Prism.util.clone(Prism.languages.markdown.url);
Prism.languages.markdown.italic.inside.url=Prism.util.clone(Prism.languages.markdown.url);
Prism.languages.markdown.bold.inside.italic=Prism.util.clone(Prism.languages.markdown.italic);
Prism.languages.markdown.italic.inside.bold=Prism.util.clone(Prism.languages.markdown.bold);

const initialValue: Descendant[] = [{ type: 'paragraph', children: [{ text: '' }, ] }];
const Editor = (props) => {
  const [value, setValue] = useState<Descendant[]>(initialValue)
  const renderLeaf = useCallback(props => <Leaf {...props} />, [])
  const editor = useMemo(() => withHistory(withReact(createEditor())), [])
  const decorate = useCallback(([node, path]) => {
    if (!Text.isText(node)) return [];
    const ranges = [], tokens = Prism.tokenize(node.text, Prism.languages.markdown);
    const getLength = token => {
      if (typeof token === 'string') return token.length;
      else if (typeof token.content === 'string') return token.content.length;
      else return token.content.reduce((l, t) => l + getLength(t), 0);
    }
    let start = 0;
    for (const token of tokens) {
      const end = start + getLength(token);
      if (typeof token !== 'string') {
        ranges.push({
          [token.type]: true,
          anchor: { path, offset: start },
          focus: { path, offset: end },
        });
      }
      start = end;
    }
    return ranges;
  }, [])
  return (
    <section className={styles.Container}>
      <Slate editor={editor} value={value} onChange={value => setValue(value)}>
        <Editable
          decorate={decorate}
          renderLeaf={renderLeaf}
          placeholder="Send A Message"
          onKeyDown={e => {
            if (e.keyCode == 13 && !e.shiftKey) {
              Send(props.User, props.SID, editor.valie);
              console.log(props);
              e.preventDefault();
            }
          }}
        />
      </Slate>
    </section>
  )
}
const Leaf = ({ attributes, children, leaf }) => {
  let Classes = [];
  if (leaf.bold) Classes.push(styles.Bold);
  if (leaf.italic) Classes.push(styles.Italic);
  if (leaf.underlined) Classes.push(styles.Underline);
  if (leaf.title) Classes.push(styles.Title);
  if (leaf.list) Classes.push(styles.List);
  if (leaf.hr) Classes.push(styles.Hr);
  if (leaf.blockquote) Classes.push(styles.Blockquote);
  if (leaf.code) Classes.push(styles.Inline_Code);
  return (<span {...attributes} className={Classes.join(' ')}>{children}</span>);
}
export default Editor;
