import React, { useEffect, useMemo, useState } from 'react';

import styles from './../Style/Components/Editor.module.css';

// Import the Slate editor factory.
import { createEditor, BaseEditor } from 'slate';
// Import the Slate components and React plugin.
import { Slate, Editable, withReact, ReactEditor } from 'slate-react';

type CustomElement = { type: 'paragraph'; children: CustomText[] };
type CustomText = { text: string };

declare module 'slate' {
  interface CustomTypes {
    Editor: BaseEditor & ReactEditor
    Element: CustomElement
    Text: CustomText
  }
}

type MyProps = { };
type MyState = { };
class Editor extends React.Component<MyProps, MyState> {
  public state: MyState;
  constructor(props) {
    super(props);
  }
  render() {
    const editor = useMemo(() => withReact(createEditor()), [])
    const [value, setValue] = useState([])
  // <span className={styles.Editor} contentEditable="true"></span>
    return (
      <section className={styles.Container}>
        <Slate
          editor={editor}
          value={value}
          onChange={newValue => setValue(newValue)}
        />
        <span className={styles.Hint}>Send A Message...</span>
      </section>
    );
  }
}
export default Editor;
