import { firestore, firebase } from './../Scripts/Firebase';

class User {
  constructor() {}

  static Send(user, SID, data) {
    console.log(data);
    // Send batch related to ratelimiting
    let batch = firestore.batch();
    batch.set(
      firestore.collection(`Servers/${SID}/Messages`).doc(),
      {
        Content: data,
        Owner: user.displayName,
        Created: firebase.firestore.FieldValue.serverTimestamp(),
        Picture: user.photoURL,
        UID: user.uid
      }
    );
    batch.set(
      firestore.collection(`Users/${user.uid}/Data`).doc('rate'),
      {
        last_write: firebase.firestore.FieldValue.serverTimestamp()
      }, { merge: true }
    );
    batch.commit().catch(() => console.log('you have been rate limited'));
  }
}

let Send = User.Send;
export {
  Send
};
