import React from 'react';

import styles from './../Style/Components/Messages.module.css';

import { firestore } from './../Scripts/Firebase';

import Message from './Message';

const test_word = `
# Markdown syntax guide

## Headers

# This is a Heading h1
## This is a Heading h2 
###### This is a Heading h6

## Emphasis

*This text will be italic*  
_This will also be italic_

**This text will be bold**  
__This will also be bold__

_You **can** combine them_

## Lists

### Unordered

* Item 1
* Item 2
* Item 2a
* Item 2b

### Ordered

1. Item 1
1. Item 2
1. Item 3
  1. Item 3a
  1. Item 3b

## Images

![This is a alt text.](/image/sample.png "This is a sample image.")

## Links

You may be using [Markdown Live Preview](https://markdownlivepreview.com/).

## Blockquotes

> Markdown is a lightweight markup language with plain-text-formatting syntax, created in 2004 by John Gruber with Aaron Swartz.
>
>> Markdown is often used to format readme files, for writing messages in online discussion forums, and to create rich text using a plain text editor.

## Tables

| Left columns  | Right columns |
| ------------- |:-------------:|
| left foo      | right foo     |
| left bar      | right bar     |
| left baz      | right baz     |

## Blocks of code

\`\`\`
let message = 'Hello world';
alert(message);
\`\`\`

## Inline code

This web site is using \`markedjs/marked\`.
`;
interface Message_Scheme {
  Content: String;
  Created: Date;
  Owner: String;
  Picture: String;
  UID: String;
}
type MyProps = { Active: { Name: string, SID: string } };
type MyState = { Messages: Map<string, Message_Scheme> };
class Messages extends React.Component<MyProps, MyState> {
  public state: MyState;
  constructor(props) {
    super(props);
    firestore.collection('Users').doc(this.props.User.uid).onSnapshot(async User => {
        if (User.exists) {
          let data = User.data();
          let Saved_Active = localStorage.getItem('Server');
          let Active: string = (Saved_Active && Object.keys(data.Servers).includes(Saved_Active)) ? Saved_Active : Object.keys(data.Servers)[0];
          let Servers: Map<string, string> = new Map(Object.entries(data.Servers));
          this.setState({ Active, Servers });
          this.props.Server_Select(Active, Servers.get(Active))
        }
        // deal with the user not having any servers
      }, () => {}
    )
  }
  render() {
    return (
      <section className={styles.Container}>
        <Message Self="mHvcak8OjqZglBlENhQSLqBOgPB3" Owner="mHvcak8OjqZglBlENhQSLqBOgPB3" Username="Spotandjake100" Picture="https://lh3.googleusercontent.com/a-/AOh14Gg6mjzn5KwNGqOcuCE9F_qS1X1Jci0vr4S3TeiTJQ=s96-c" Content={test_word}/>
        <Message Self="mHvcak8OjqZglBlENhQSLqBOgPB3" Owner="mHvcak8OjqZglBlENhQSLqBOgPB3" Username="Spotandjake100" Picture="https://lh3.googleusercontent.com/a-/AOh14Gg6mjzn5KwNGqOcuCE9F_qS1X1Jci0vr4S3TeiTJQ=s96-c" Content="test"/>
      </section>
    );
  }
}
export default Messages;
