import React from 'react';
import styles from './../Style/Components/Servers.module.css';
import { firestore } from './../Scripts/Firebase';
//Modules 
import Server from './Server';
type MyProps = { Server_Select: Function, User: any };
type MyState = { Active: string, Servers: Map<string, string> };
class Servers extends React.Component<MyProps, MyState> {
  public state: MyState;
  constructor(props) {
    super(props);
    // create the snapshow listener
     firestore.collection('Users').doc(this.props.User.uid).onSnapshot(async User => {
        if (User.exists) {
          let data = User.data();
          let Saved_Active = localStorage.getItem('Server');
          // let Active = (Saved_Active && data.Servers.includes()
          console.log(data);
        } 
        // give them the default one later
        // else {
        //   // Make the user a profile with the basic server
        //   let Server_Info = async (Server_ID)  => {
        //     let Document = firestore.collection('Servers').doc(Server_ID);
        //     let doc = await Document.get().catch(() => 'Banned');
        //     return {
        //       Name: doc && doc != 'Banned' && doc.exists ? doc.data().Name : null,
        //       Banned: doc == 'Banned' ? true : false
        //     }
        //   }
        //   let SID = 'UUw40MdRDcUxdEy00Zuo';
        //   let Data = await Server_Info(SID, db);
        //   if (!Data.Banned) {
        //     db.collection('Users').doc(user.uid).set({
        //       Servers: { [SID]: Data.Name }
        //     }).catch(err => { console.log(err)});
        //   }
        // }
      }, () => {}
    )
    // state
    // this.state = {
    //   Active: '123',
    //   Servers: new Map([
    //     [ '123', 'Testing' ],
    //     [ 'UUw40MdRDcUxdEy00Zuo', 'Tes' ]
    //   ])
    // };
    this.props.Server_Select(
      this.state.Active,
      this.state.Servers.get(this.state.Active)
    )
  }
  Change_Server(evt, child) {
    this.setState({Active: child});
    this.props.Server_Select(
       child,
      this.state.Servers.get(child)
    )
  }
  render() {
    return (
      <section className={styles.Container}>
        {
          (() => {
            let Comps = [];
            for (let [key, value] of this.state.Servers) {
              Comps.push(
                <Server 
                  key={key}
                  id={key}
                  Name={value}
                  Active={this.state.Active}
                  customClickEvent={this.Change_Server.bind(this)}
                />
              );
            }
            return Comps;
          })()
        }
        <Server id={this.props.User.uid} Name={this.props.User.displayName} Active={this.state.Active} customClickEvent={this.Change_Server.bind(this)} Self={this.props.User.photoURL}/>
      </section>
    );
  }
}

export default Servers;
