import React from 'react';

import styles from './../Style/Components/Message.module.css';

import Marked from './../Scripts/Marked';

type MyProps = { 
  Self:     string,
  Owner:    string,
  Username: string,
  Picture:  string,
  Content:  string
};
type MyState = { Content: string };
class Message extends React.Component<MyProps, MyState> {
  public state: MyState;
  constructor(props) {
    super(props);
    this.state = {
      Content: Marked(this.props.Content)
    }
  }
  render() {
    return (
      <div className={styles.Container} Self={this.props.Self == this.props.Owner}>
        <picture className={styles.Icon}>
          <img className={styles.Icon_IMG} src={this.props.Picture} />
        </picture>
        <div className={styles.Content} dangerouslySetInnerHTML={{ __html: this.state.Content }}></div>
      </div>
    );
  }
}
export default Message;
