import React from 'react';

import styles from './../Style/Components/Messages.module.css';

import { firestore } from './../Scripts/Firebase';

import Message from './Message';

type MyProps = { SID: string, User: any };
type Message_Entry = { Time: number, Msg: Message };
type MyState = { Messages: Map<string, Message_Entry> };
class Messages extends React.Component<MyProps, MyState> {
  public state: MyState = { Messages: new Map() }
  constructor(props) {
    super(props);
    let UID: string = this.props.User.uid;
    firestore.collection(`Servers/${this.props.SID}/Messages`).limit(7).orderBy('Created', 'desc')
      .onSnapshot(async Server => {
        let Msgs: Map<string, Message_Entry> = this.state.Messages;
        Server.docChanges().forEach((Msg) => {
          if (Msg.type != 'removed') {
            const Data = Msg.doc.data();
            if (!Data.Created) return;
            Msgs.set(Msg.doc.id, {
              Time: Data.Created.toMillis(),
              Msg: (
                <Message 
                  key={Msg.doc.id}
                  Self={UID}
                  Owner={Data.UID}
                  Username={Data.Owner}
                  Picture={Data.Picture}
                  Content={Data.Content}
                />
              )
            })
          } else {
            Msgs.delete(Msg.doc.id);
          }
        });
        this.setState({ 
          Messages: new Map([...Msgs.entries()].sort((a, b) => b[1].Time-a[1].Time))
        });
      }, () => {}
    )
  }
  render() {
    return (
      <section className={styles.Container}>
        {[...this.state.Messages].map(e => e[1].Msg)}
      </section>
    );
  }
}
export default Messages;
