import React from 'react';

import styles from './Style/Main.module.css';

// Components
import Servers from './Components/Servers';
import Channels from './Components/Channels';
import Content from './Components/Content';

type MyProps = { User: any };
type MyState = { Active: { Name: string, SID: string } };
class Main extends React.Component<MyProps, MyState> {
  public state: MyState;
  constructor(props) {
    super(props);
    this.state = {
      Active: {
        Name: '',
        SID: '0'
      }
    }
  }
  Change_Server(id, name) {
    this.setState({
      Active: {
        Name: name,
        SID: id
      }
    });
  }
  render() {
    return (
      <div className={styles.Container}>
        <Servers Server_Select={this.Change_Server.bind(this)} />
        <Channels />
        <Content Active={this.state.Active}/>
      </div>
    );
  }
}

export default Main;
