import React, {useMemo, useState} from 'react';
import { createEditor } from 'slate';
import { Slate, Editable, withReact } from 'slate-react';

import styles from './../Style/Components/Editor.module.css';

type MyProps = { };
type MyState = { };
class Input extends React.Component<MyProps, MyState> {
  public state: MyState;
  constructor(props) {
    super(props);
  }
  render() {
    const initialState = [{type: 'paragraph', children: [{ text: "edit me"}]}]
    const editor = createEditor();
    const [value, setValue] = useState(initialState)
    // <span className={styles.Editor} contentEditable="true"></span>
    return (
      <section className={styles.Container}>
        <Slate editor={editor} value={value} onChange={newVal => setValue(newVal)}>
          <Editable />
        </Slate>
        <span className={styles.Hint}>Send A Message...</span>
      </section>
    );
  }
}

export default Input;
