import React, { useState } from 'react';

import styles from './../Style/Components/Editor.module.css';

//slate
import { Editor } from 'slate-react'
import Plain from 'slate-plain-serializer'

const initialValue = Plain.deserialize('Hello World')

type MyProps = { };
type MyState = { };
class Erdito extends React.Component<MyProps, MyState> {
  public state: MyState;
  constructor(props) {
    super(props);
  }
  render() {
    const [value, setValue] = useState(initialValue);
  // <span className={styles.Editor} contentEditable="true"></span>
    return (
      <section className={styles.Container}>
        <Editor
          value={value}
          onChange={(change) => setValue(change.value)}
        />
        <span className={styles.Hint}>Send A Message...</span>
      </section>
    );
  }
}
export default Editor;
