import Marked from 'marked';
import hljs from "highlight.js";
Marked.setOptions({
  highlight: (c, l) => 
    hljs.getLanguage(l)?hljs.highlight(l, c).value:hljs.highlightAuto(c).value,
  smartLists: true,
  sanitize: true,
  silent: true
});
Marked.use({ 
  renderer: {
    image(href, title, text) {
      try {
        href = encodeURI(href).replace(/%25/g, '%');
      } catch (e) { href = null }
      if (href == null) return text;
      switch (true) {
        case /\.(wav|mp3)$/i.test(href):
        case text == 'Music':
          return `<audio-player src="${href}"alt="Image"${title?`title="${title}"`:''}></audio-player>`
        case text == 'Video':
          break;
        case /\.(gif|jpe?g|tiff?|png|webp|bmp)$/i.test(href):
        default:
        case text == 'Image':
          return `
            <picture>
              <img is="view-img"src="${href}" height="500" alt="Image"${title?`title="${title}"`:''}>
            </picture>
          `;
      }
    }
  }
});
export default Marked;
